/*      Abstract: The custom MKAnnotation object representing a venue.  */#import "Venue2Annotation.h"@implementation Venue2Annotation@synthesize latitude;@synthesize longitude;- (CLLocationCoordinate2D)coordinate;{    CLLocationCoordinate2D theCoordinate;    theCoordinate.latitude = 40.741022;    theCoordinate.longitude = -73.989947;    return theCoordinate; }- (NSString *)title{    return @"The Flatiron Building";}// optional- (NSString *)subtitle{    return @"Flatiron was it's nickname, but it stuck";}@end
